import './ThemeSelector.css'

import { useTheme } from '../hooks/useTheme'
import modeIcon from '../image/mode-icon.svg'

const colors = ['purple', 'orange', 'green']

export default function ThemeSelector() {
    const {changeColor, changeMode, mode} = useTheme()
    const toggleMode = () => {
        changeMode(mode==='dark'?'light':'dark')
    }
    return (
        <div className='theme-selector'>
            <div className='mode-toggle'>
                <img onClick={toggleMode} src={modeIcon} style={{filter:mode==='dark'?'invert(100%':'invert(20%)'}} alt='dark/light toggle icon' />
            </div>
            <div className='theme-buttons'>
                {colors.map(color => (
                    <div key={color} onClick={() => changeColor(color)} style={{background: color}} />
                ))}
            </div>
        </div>
    )
}
